import React from "react"
import axios from 'axios'
import './contact.css';
class Contact extends React.Component{
    constructor(props){
        super(props)
        this.state={
            contactinfo:this.props.data,
            ipUser:"",
            ipPhone:"",
            ipEmail:"",
           
            update:false,
            uid:-1,
            results:[],
            statusText:''
        }
    }

    componentDidMount=()=>{
        axios.get('http://localhost:3001/empDetails').then((res)=>this.setState({results:res.data})).catch((err)=>console.log(err))
      }

    createUser=(e)=>{
        e.preventDefault()
        axios.post('http://localhost:3001/empDetails',{
            fname:'karan',
            email:'karan@gmail.com',
            phone:289828
        }).then((res)=>this.setState({statusText:"created user successfully"})).catch((err)=>this.setState({statusText:'An Error Occured '}))
    }

    deleteUser=(e,itemid)=>{
        e.preventDefault()
        axios.delete(`http://localhost:3001/empDetails/${itemid}`).then((res)=>this.setState({statusText:"deleted"})).catch((err)=>console.log(err))
        axios.get('http://localhost:3001/empDetails').then((res)=>this.setState({results:res.data})).catch((err)=>console.log(err))
    }
    updateUser=(e,itemid)=>{
        axios.patch(`http://localhost:3001/empDetails/${itemid}`,{
            fname:'Peter'
        }).then((res)=>this.setState({statusText:"updated"})).catch((err)=>console.log(err))
    }

   handleChange=(e,key)=>{
    if(key==='uname')
    this.setState({ipUser:e.target.value})
    if(key==='phone')
    this.setState({ipPhone:e.target.value})
    if(key==='email')
    this.setState({ipEmail:e.target.value})
    if(key==='addr')
    this.setState({ipAddress:e.target.value})    
   }

    
    handleContact=(e)=>{
        e.preventDefault()
        axios.post('http://localhost:3001/empDetails').then((res)=>{return res.json()}).then((res)=>this.setState({results:res})).catch((err)=>console.log(err))
        const obj={
            id:Date.now(),
            fname:this.state.ipUser,
            phone:this.state.ipPhone,
            email:this.state.ipEmail,
            address:this.state.ipAddress
        }
        let newData=[...this.state.contactinfo,obj]
        this.setState({contactinfo:newData})   
    }
    handleDelete=(e,itemid)=>{
      const result=this.state.contactinfo.filter((item)=>item.id!==itemid)
      this.setState({contactinfo:result})
    }
    toggleUpdate=()=>{
        this.setState({update:!this.state.update})
    }
    setUpdateMsg=(e,itemid)=>{
        e.preventDefault()
        this.toggleUpdate()   
        this.setState({uid:itemid})
    }
    handleUpdate=(e)=>{
        e.preventDefault()
        let temp=[...this.state.contactinfo]
        let obj=temp.find((item)=>item.id===this.state.uid)
        obj.fname=this.state.ipUser
        this.setState({contactinfo:temp})
        this.setState({uid:-1})
        this.toggleUpdate()
    }
    render(){
        return(
            <div>
                <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
                <a className="navbar-brand" href="#">Welcome User </a>
                <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
                  <span class="navbar-toggler-icon"></span>
                </button>
                <div class="collapse navbar-collapse" id="navbarNavAltMarkup">
                  <div class="navbar-nav">
                    <a class="nav-item nav-link " href="add.html" >Login</a>
                    <a class="nav-item nav-link " href="add.html" >Cart</a>
                    
                  </div>
                </div>
              </nav>
              


                {
                this.state.update?<h1>Enter your new values</h1>:<h1></h1>
                }
                     
                <form>
                Username:<input type='text' onChange={(e)=>this.handleChange(e,'uname')}></input>
                Phone:<input type='text' onChange={(e)=>this.handleChange(e,'phone')}></input>
                Email:<input type='text' onChange={(e)=>this.handleChange(e,'email')}></input>
                Address:<input type='text' onChange={(e)=>this.handleChange(e,'addr')}></input>
                {
                    this.state.update?<button className="btn2" onClick={(e)=>this.handleUpdate(e)}>update contact</button>:<button  className="btn2"onClick={(e)=>this.handleContact(e)}>add contact</button>
                }
                </form>
                
            {/* All Contacts: */}
            <div className="container">
            <div className="row">
            {
                this.state.contactinfo.map((item)=>(
                    <div className="card hover-shadow cursor">
                    <div class="card-body ">
                        <h2>{item.fname}</h2>
                        <h3>{item.phone} {item.email}</h3>
                        <p>{item.address}</p>
                        <button onClick={(e)=>this.handleDelete(e,item.id)}>delete</button>
                        <button onClick={(e)=>this.setUpdateMsg(e,item.id)}>update</button>
                    </div>
                    </div>
                ))
            }
            </div>
            </div>
            </div>
        )
    }
}
export default Contact
