import React from 'react';
import {Dash} from './dash';

export class Main extends React.Component
{
    constructor(props)
    {
        super(props)
        this.state={
            loggedin:true,uname:"Amrutha"
        }
    }
    toggleUser=()=>{
        this.setState({loggedin:!(this.state.loggedin)})
    }
    
    render(){
        return(
            <div>
               {
                this.state.loggedin?
                <>
                <Dash></Dash>
                </>:
                <>
                <h2>Please sign in !</h2>
                <Signin></Signin>
                </>
               } 
            </div>
        )
    }
}
