import React from 'react';
import { Signin } from './signin';


export class Dash extends React.Component
{
    constructor(props)
    {
        super(props)
        this.state={
            login:false,uname:"amrutha"
        }
    }
    switchtoLogout=()=>
    {
        this.setState({login:false})
    }
    switchLogin=()=>
    {
        this.setState({login:true})
    }
    toggleUser=()=>{
        this.setState({isLoggedIn:!(this.state.isLoggedIn)})
    }

    render()
    {
        return(
            <div>
               {
    
            this.state.login?<>
            <nav class="navbar navbar-expand-lg navbar-dark bg-dark">
            <a class="navbar-brand" href="#">Welcome  {this.state.uname}</a>
            <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
              <span class="navbar-toggler-icon"></span>
            </button>
            <div class="collapse navbar-collapse" id="navbarNavAltMarkup">
              <div class="navbar-nav">
                <a class="nav-item nav-link " href="add.html" style={{marginLeft:"1700%"}}>About</a>
                
              </div>
            </div>
          </nav>
            {/* <h2>Welcome {this.state.uname}</h2> */}
            <button onClick={this.toggleUser}>Logout</button>
            </>:<>
                <h2>Please Sign In to continue...</h2>
                <Signin></Signin>
                </>
               } 
            </div>
        )
    }
}
