import React from "react";
import './whoc.css';
class Eproducts extends React.Component{
    constructor(props){
        super(props)
        this.state={
            productDetails:[],
            search:'',
            result:[]
        }
    }
    static getDerivedStateFromProps(props,state){
        return {productDetails:props.data}
    }
    handleChange=(event)=>{
        // console.log(event.target.value)
        this.setState({search:event.target.value})
    }
    handleSearch=(event)=>{
        event.preventDefault()
        let data=this.state.productDetails.filter((item)=>item.pName===this.state.search)
        this.setState({result:data}) 
    }
    render(){
      
        return(
            <div>
                <nav class="navbar navbar-expand-lg navbar-dark bg-dark">
                <a class="navbar-brand" href="#">Welcome User </a>
                <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
                  <span class="navbar-toggler-icon"></span>
                </button>
                <div class="collapse navbar-collapse" id="navbarNavAltMarkup">
                  <div class="navbar-nav">
                    <a class="nav-item nav-link " href="add.html" style={{marginLeft:"60%"}}></a>
                    
                  </div>
                </div>
              </nav>

              
             
                <form>
                    <input type='text' placeholder="Search here..." onChange={(e)=>this.handleChange(e)}></input>
                    <button onClick={(e)=>this.handleSearch(e)}>search</button>
                </form>
                {
                    this.state.result.map((item)=>(
                    <div className="container-sm">
                        <div className="row col-sm-8">
                                <div className="card">
                                    <div class="card-body ">
                                        <img src={item.image}/>
                                        {/* <h2>{item.pName}</h2>
                                        <p>Price:{item.price}</p>
                                        <p>Manufacturer:{item.manufacturer}</p>
                                        <p>Delivery:{item.expectedDelivery}</p>
                                        <a href="https://www.amazon.in/Noise-Wireless-Equalizer-Resistance-Bluetooth/dp/B098R25TGC/ref=sr_1_7?crid=23X5KDGZLK70A&keywords=earpods&qid=1659335044&sprefix=earpids%2Caps%2C368&sr=8-7&th=1"><button className="btn">Buy</button></a> */}
                                    </div>
                              </div>
                        </div>
                     </div>
                
                        
                    ))
                }
                
                
            </div>
        )
    }
}
export default Eproducts
